import{_ as t,o as e,c as a,R as r}from"./chunks/framework.32aacd6d.js";const f=JSON.parse('{"title":"WP1部分","description":"","frontmatter":{},"headers":[],"relativePath":"write/wp1/witre_p1.md","filePath":"write/wp1/witre_p1.md","lastUpdated":1686065644000}'),l={name:"write/wp1/witre_p1.md"},n=r('<h1 id="wp1部分" tabindex="-1">WP1部分 <a class="header-anchor" href="#wp1部分" aria-label="Permalink to &quot;WP1部分&quot;">​</a></h1><hr><h1 id="做题之前" tabindex="-1">做题之前 <a class="header-anchor" href="#做题之前" aria-label="Permalink to &quot;做题之前&quot;">​</a></h1><div class="warning custom-block"><p class="custom-block-title">注意事项</p><p>读题看图，注意时态，分析数据</p></div><h1 id="行文结构" tabindex="-1">行文结构 <a class="header-anchor" href="#行文结构" aria-label="Permalink to &quot;行文结构&quot;">​</a></h1><table><thead><tr><th style="text-align:center;">段落</th><th style="text-align:center;">句数</th><th style="text-align:center;">描述</th></tr></thead><tbody><tr><td style="text-align:center;">开头</td><td style="text-align:center;">1句</td><td style="text-align:center;">对题目进行同义替换改写</td></tr><tr><td style="text-align:center;">概括段</td><td style="text-align:center;">1~2句</td><td style="text-align:center;">抓住最明显的特征或整体趋势</td></tr><tr><td style="text-align:center;">主体段1</td><td style="text-align:center;">2~3句</td><td style="text-align:center;">对数据分类并做比较</td></tr><tr><td style="text-align:center;">主体段2</td><td style="text-align:center;">2~3句</td><td style="text-align:center;">若有第二张图表</td></tr></tbody></table><h2 id="开头" tabindex="-1">开头 <a class="header-anchor" href="#开头" aria-label="Permalink to &quot;开头&quot;">​</a></h2><p>开头段应用一句话简明介绍图标描述的为何物，可对题目进行同义替换来行文</p><h3 id="例子" tabindex="-1">例子 <a class="header-anchor" href="#例子" aria-label="Permalink to &quot;例子&quot;">​</a></h3><p>J5T1例子</p><blockquote><p>The graph below shows the proportion of the population aged 65 and over between 1940 and 2040 in three different countries. (J5T1)</p></blockquote><p>对其进行改写</p><blockquote><p>The line graph compares the percentage of older people aged 65 or more from 1940 to 2040 in Japan, the USA and Sweden.</p></blockquote><h2 id="概括段" tabindex="-1">概括段 <a class="header-anchor" href="#概括段" aria-label="Permalink to &quot;概括段&quot;">​</a></h2><p>概括段(Overview)，位于全文的第二段，通常为1句或2句。写此段时，应该找出图表中两个最显眼的特征(如趋势，极值)</p><h3 id="例子-1" tabindex="-1">例子 <a class="header-anchor" href="#例子-1" aria-label="Permalink to &quot;例子&quot;">​</a></h3><p>J5T1 Overview</p><blockquote><p>It is clear that there was and will be a clear difference in the percentage of people aged 65 or more in these three countries. And the population aged 65 and over in Japan is expected to be at the top in 2040.</p></blockquote><h3 id="讲解" tabindex="-1">讲解 <a class="header-anchor" href="#讲解" aria-label="Permalink to &quot;讲解&quot;">​</a></h3><div class="warning custom-block"><p class="custom-block-title">注意事项</p><p>此段注意不要出现具体的数字</p></div><p>可以以下句式开头</p><ul><li>It&#39;s clear that...</li><li>Overall, we can see that...</li></ul><p>此段可用的一些句式</p><table><thead><tr><th style="text-align:center;">A</th><th style="text-align:center;">B</th></tr></thead><tbody><tr><td style="text-align:center;">数值</td><td style="text-align:center;">the figures for<br>the number</td></tr><tr><td style="text-align:center;">在变化，波动</td><td style="text-align:center;">...fluctuate(v.)</td></tr><tr><td style="text-align:center;">达到最高值</td><td style="text-align:center;">...peaked at<br>...reach the peak at<br>at the top</td></tr><tr><td style="text-align:center;">呈现向上向下趋势</td><td style="text-align:center;">showed an upward/downward trend</td></tr><tr><td style="text-align:center;">呈现不一样的趋势</td><td style="text-align:center;">there was a different trend in...</td></tr><tr><td style="text-align:center;">有明显的不一样</td><td style="text-align:center;">there was a clear difference in...</td></tr></tbody></table><h2 id="主体部分" tabindex="-1">主体部分 <a class="header-anchor" href="#主体部分" aria-label="Permalink to &quot;主体部分&quot;">​</a></h2><p>主体部分主要对数据分类并做比较</p><h3 id="的表达" tabindex="-1">%的表达 <a class="header-anchor" href="#的表达" aria-label="Permalink to &quot;%的表达&quot;">​</a></h3><p>班上的40%的同学是男生</p><ul><li>40% of students in this class are boys</li><li>the proportion of boys is low at 40%</li><li>the figure for boy is low (40% only)</li><li>boys account for 40% of students</li></ul><h3 id="增强文章的连续性" tabindex="-1">增强文章的连续性 <a class="header-anchor" href="#增强文章的连续性" aria-label="Permalink to &quot;增强文章的连续性&quot;">​</a></h3><table><thead><tr><th>表达</th><th>选择</th></tr></thead><tbody><tr><td>连接词</td><td>表示对比：in contrast, on the other hand, by comparison; 表示相似：also, similarly</td></tr><tr><td>代词 this 连接</td><td>The figure for this country …</td></tr><tr><td>突出趋势的改变或者高低的变化</td><td>a different / a similar / the same trend</td></tr><tr><td>连词</td><td>表示并列：and; 表示转折和对比：while, whereas</td></tr></tbody></table>',31),d=[n];function i(o,h,s,c,p,b){return e(),a("div",null,d)}const x=t(l,[["render",i]]);export{f as __pageData,x as default};
